import {
    useState,
    useEffect,
  } from "react";
function Calculator(){
    const [number, count] = useState(function(){
        const lsNumber =localStorage.getItem('number');
        return lsNumber ? JSON.parse(lsNumber) : 0;
      });
      const increment = () =>{
        count(number + 1);
      }
      const decrement = () =>{
        if (number > 0){
          count(number - 1);
        }
      }
      const returnDefault = () =>{
        count(number - number);
      }
      useEffect(function(){
        localStorage.setItem('number', JSON.stringify(number));
      },[number]);
    return (
        <div className="container">
          <h1>number: {number}</h1>
          <button className="DecrementButton" onClick={decrement}>   <svg width="16" height="2" viewBox="0 0 16 2" fill="none" xmlns="http://www.w3.org/2000/svg">
          <line y1="1" x2="16" y2="1" stroke="#0064FE" stroke-width="2" class="icon" />
        </svg></button>
          <button className="GoToDefaultButton" onClick={returnDefault}>go to default</button>
          <button className="IncrimentButton" onClick={increment}>+</button>
        </div>
      );
};

export default Calculator;
