import {
    useState,
    useEffect,
    useContext,
  } from "react";

function IncDec(data){
    const [number, count] = useState(function(){
        const lsNumber =localStorage.getItem('number');
        return lsNumber ? JSON.parse(lsNumber) : 0;
      });
      const increment = () =>{
        count(number + 1);
      }
      const decrement = () =>{
        if (number > 0){
          count(number - 1);
        }
      }
      const returnDefault = () =>{
        count(number - number);
      }
      useEffect(function(){
        localStorage.setItem('number', JSON.stringify(number));
      },[number]);
}
export default IncDec;
