import {
    useState,
    useEffect,
    useContext,
  } from "react";
function Calculator(){
    const [number, count] = useState(function(){
        const lsNumber =localStorage.getItem('number');
        return lsNumber ? JSON.parse(lsNumber) : 0;
      });
      const increment = () =>{
        count(number + 1);
      }
      const decrement = () =>{
        if (number > 0){
          count(number - 1);
        }
      }
      const returnDefault = () =>{
        count(number - number);
      }
      useEffect(function(){
        localStorage.setItem('number', JSON.stringify(number));
      },[number]);
    return (
        <div>
          <h1>number: {number}</h1>
          <button onClick={decrement}>-</button>
          <button onClick={returnDefault}>0</button>
          <button onClick={increment}>+</button>
        </div>
      );
};

export default Calculator;
