import {
  useState,
  useEffect,
  useContext,
} from "react";

function App() {
  //const [defaultNumber, setCount] = useState(0);
  const [number, setNumber] = useState(0);

  // useEffect to save the number value to localStorage when it changes
  useEffect(() => {
    localStorage.setItem('savedNumber', JSON.stringify(number));
  }, [number]);

  const handleIncrement = () => {
    setNumber(prevNumber => prevNumber + 1);
  };

  const handleDecrement = () => {
    if (number > 0) {
      setNumber(prevNumber => prevNumber - 1);
    }
  };

  return (
    <div>
      <h1>Number: {number}</h1>
      <button onClick={handleIncrement}>Increment</button>
      <button onClick={handleDecrement} disabled={number === 0}>
        Decrement
      </button>
    </div>
  );
};

export {App};
