import {
  useState,
  useEffect,
  useContext,
} from "react";

function App() {
  const [number, intIncriment, intDecriment] = useState(0);
  const incriment = () =>{
    intIncriment(number + 1);
  }
  const decriment = () =>{
    intDecriment(number - 1);
  }
  return (
    <div>
      <h1>number: {number}</h1>
      <button onClick={intIncriment}>+</button>
      <button onClick={intDecriment}>-</button>
    </div>
  );
}

export {App};
