import {
  useState,
  useEffect,
  useContext,
} from "react";

function App() {
  const [number, count] = useState(0);
  const incriment = () =>{
    count(number + 1);
  }
  const decriment = () =>{
    if (count > 0){
      count(number - 1);
    }
  }
  return (
    <div>
      <h1>number: {number}</h1>
      <button onClick={incriment}>+</button>
      <button onClick={decriment}>-</button>
    </div>
  );
}

export {App};
