import {
  useState,
  useEffect,
  useContext,
} from "react";
import Calculator from "./components/Calculator";

function App() {
  const [number, count] = useState(function(){
    const lsNumber =localStorage.getItem('number');
    return lsNumber ? JSON.parse(lsNumber) : 0;
  });
  const increment = () =>{
    count(number + 1);
  }
  const decrement = () =>{
    if (number > 0){
      count(number - 1);
    }
  }
  const returnDefault = () =>{
    count(number - number);
  }
  useEffect(function(){
    localStorage.setItem('number', JSON.stringify(number));
  },[number]);
  return (
    <div>
      <Calculator/>
    </div>
  );
}

export {App};
